sys_ui_page.client_script:
//NOTE: To directly access the record Use this URL:
//https://dev65352.service-now.com/incident.do?sys_id=d71f7935c0a8016700802b64c67c11c6

jQuery('#myModifyAndAttachPDFButton').click(function () {
	var myHardcodedIncidentRecordName = "d71f7935c0a8016700802b64c67c11c6";
	var ga = new GlideAjax('myPDFGeneratorScriptInclude'); //this is server side object name 
	ga.addParam('sysparm_name','modifyAndAttachPDF'); //this is the method name to call on the object
	ga.addParam('sysparm_my_own_parameter_1', myHardcodedIncidentRecordName);
	ga.getXML(pdfGeneratedCallback);

});

//MUST ALWAYS use a lower-case 'j' in  jQuery calls.    Otherwise can fail silently (or barely noticeable in the Dev Console of the Browser...)
function pdfGeneratedCallback(response) {
	var answer = response.responseXML.documentElement.getAttribute('answer');
	jQuery("#msg").append("<p> PDF has been created. Content From Server: ").append(answer).append("</p>");
}
